void drawLadder(tContext sContext, uint16_t x, uint16_t y)
{
    int i, j;
	int numberOfPixels;
    uint32_t ladderOneChannel[1260];

	numberOfPixels = sizeof(ladder)/sizeof(unsigned char);
	j = 0;
    for (i = 0; i < numberOfPixels - 3; i+=3)
    {
        ladderOneChannel[j] = (ladder[i]<<16) + (ladder[i+1]<<8) + (ladder[i+2]);
        j++;
    }
    
    GrContextBackgroundSet(&sContext, ClrBlack);
    
	
    for (i = 0; i < 20; i++)
    {
        for (j = 0; j < 21; j++)
        {
            GrContextForegroundSet(&sContext, ladderOneChannel[i*21 + j]);
            GrPixelDraw(&sContext,j+x,i+y);
        }
    }
}

void Ladder(tContext sContext)
{
	uint8_t i;
	uint16_t x[] = {90, 30, 60}, y[] = {90, 70, 30};
	
	for (i = 0; i < 3; i++)
		drawLadder(sContext, x[i], y[i]);
}
