void drawLatter(tContext sContext)
{
    int i, j;
	int numberOfPixels;
    uint32_t latterOneChannel[1260];

	numberOfPixels = sizeof(latter)/sizeof(unsigned char);
	j = 0;
    for (i = 0; i < numberOfPixels - 3; i+=3)
    {
        latterOneChannel[j] = (latter[i]<<16) + (latter[i+1]<<8) + (latter[i+2]);
        j++;
    }
    
    GrContextBackgroundSet(&sContext, ClrBlack);
    
	
    for (i = 0; i < 20; i++)
    {
        for (j = 0; j < 21; j++)
        {
            GrContextForegroundSet(&sContext, latterOneChannel[i*21 + j]);
            GrPixelDraw(&sContext,j+90,i+90);
        }
    }
}