uint32_t nameOneChannel[1590];
void drawName(void)
{
    int i, j;
	int numberOfPixels;

	numberOfPixels = sizeof(name)/sizeof(unsigned char);
	j = 0;
    for (i = 0; i < numberOfPixels - 3; i+=3)
    {
        nameOneChannel[j] = (name[i]<<16) + (name[i+1]<<8) + (name[i+2]);
        j++;
    }
    
    GrContextBackgroundSet(&sContext, ClrBlack);
    
	
    for (i = 0; i < 10; i++)
    {
        for (j = 0; j < 53; j++)
        {
            GrContextForegroundSet(&sContext, nameOneChannel[i*53 + j]);
            GrPixelDraw(&sContext,j+43,i+10);
        }
    }
}

void Name(void)
{
	while(1)
	{
		osMutexWait(context_mutex, osWaitForever);
		drawName();
		osMutexRelease(context_mutex);

	}
}
